import React, { useState, useEffect } from 'react';

const RecentContent = ({ influencerId }) => {
    const [contentItems, setContentItems] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Placeholder content for when no influencer is selected
    const placeholderContent = [
        {
            id: 1,
            title: "Post 1 - 56% Vibes",
            content: "Celebrity gains support with latest post."
        },
        {
            id: 2,
            title: "Post 2 - 44% Vibes",
            content: "Recent news affecting vibes significantly."
        },
        {
            id: 3,
            title: "Post 3 - 95% Vibes",
            content: "Celebrity's latest appearance is a major hit."
        }
    ];

    useEffect(() => {
        const fetchContent = async () => {
            if (!influencerId) {
                setContentItems(placeholderContent);
                setLoading(false);
                return;
            }

            try {
                setLoading(true);
                const response = await fetch('http://127.0.0.1:8000/Videos');

                if (!response.ok) {
                    throw new Error('Failed to fetch videos');
                }

                const data = await response.json();

                // Filter videos by influencer_id and get the most recent 3 items
                const filteredContent = data
                    .filter(item => item.influencer_id === influencerId)
                    .sort((a, b) => b.id - a.id)
                    .slice(0, 3)
                    .map(item => ({
                        id: item.id,
                        title: item.title,
                        content: `Views: ${item.views}`,
                        url: item.url
                    }));

                setContentItems(filteredContent.length > 0 ? filteredContent : placeholderContent);
            } catch (err) {
                console.error('Error fetching videos:', err);
                setError('Failed to load videos');
                setContentItems(placeholderContent);
            } finally {
                setLoading(false);
            }
        };

        fetchContent();
    }, [influencerId]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="content-section">
            {contentItems.map(item => (
                <div key={item.id} className="content-item">
                    <h3>{item.title}</h3>
                    <p>{item.content}</p>
                    {item.url && (
                        <a href={item.url} target="_blank" rel="noopener noreferrer">
                            Watch on YouTube
                        </a>
                    )}
                </div>
            ))}
        </div>
    );
};

export default RecentContent;
