// NewsSection.js
import React, { useState, useEffect } from 'react';

const NewsSection = ({ influencerId }) => {
  const [newsItems, setNewsItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        setLoading(true);
        const response = await fetch('http://localhost:8000/content');
        if (!response.ok) {
          throw new Error('Failed to fetch content');
        }
        const data = await response.json();

        // Filter content by influencer_id and get the most recent 3 items
        const filteredContent = data
          .filter(item => item.influencer_id === influencerId)
          .sort((a, b) => b.id - a.id) // Sort by id in descending order (assuming newer content has higher id)
          .slice(0, 3) // Get only the first 3 items
          .map(item => ({
            id: item.id,
            title: item.title,
            content: `Platform: ${item.platform}`,
            url: item.url
          }));

        setNewsItems(filteredContent);
      } catch (err) {
        console.error('Error fetching content:', err);
        setError('Failed to load news content');
      } finally {
        setLoading(false);
      }
    };

    // Only fetch if we have an influencer ID
    if (influencerId) {
      fetchContent();
    } else {
      setNewsItems([]); // Clear news items if no influencer is selected
    }
  }, [influencerId]); // Re-fetch when influencer ID changes

  if (loading) return <div>Loading news...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!influencerId) return null;

  return (
    <section className="news-section">
      <h3>What's in the news?</h3>
      {newsItems.length > 0 ? (
        newsItems.map((item) => (
          <div key={item.id} className="news-item">
            <h4>{item.title}</h4>
            <p>{item.content}</p>
            <a 
              href={item.url} 
              target="_blank" 
              rel="noopener noreferrer"
              className="read-more-button"
            >
              Read More
            </a>
          </div>
        ))
      ) : (
        <p>No recent content available for this influencer.</p>
      )}
    </section>
  );
};

export default NewsSection;
