import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Homepage.css";

const influencers = [
  { id: 1, name: "Taylor Swift", image: "https://cdn.prod.website-files.com/61e1db1c178256e11be974b0/63f91e8829fbd0aaf9de4cb8_htygWn9bAtmW8KF52-4zwtXFzTwiA9dfZ4Q_custom_logo.png" },
  { id: 2, name: "Kanye West", image: "https://upload.wikimedia.org/wikipedia/commons/thumb/5/5c/Kanye_West_at_the_2009_Tribeca_Film_Festival_%28crop_2%29.jpg/1200px-Kanye_West_at_the_2009_Tribeca_Film_Festival_%28crop_2%29.jpg" },
  // Add the rest of the influencers here...
];

const Homepage = ({ onSelectInfluencer }) => {
  const [showOverlay, setShowOverlay] = useState(true); // State to control overlay visibility
  const navigate = useNavigate();

  const handleClick = (influencer) => {
    onSelectInfluencer(influencer.id);
    navigate("/profile");
  };

  useEffect(() => {
    const timer = setTimeout(() => setShowOverlay(false), 3000); // Hide overlay after 3 seconds

    if (showOverlay) {
      document.querySelector(".homepage-container").classList.add("blur");
    } else {
      document.querySelector(".homepage-container").classList.remove("blur");
    }

    return () => clearTimeout(timer); // Cleanup timer
  }, [showOverlay]);

  return (
    <div className="homepage-container">
      {/* Logo in top left */}
      <div className="vibecheck-logo">Vibe Check</div>

      {/* Welcome overlay */}
      {showOverlay && (
        <div className="welcome-overlay">
          <h1>Welcome to Vibe Check</h1>
        </div>
      )}

      <h1>Select an Influencer</h1>
      <div className="influencer-grid">
        {influencers.map((influencer) => (
          <div
            key={influencer.id}
            className="influencer-card"
            onClick={() => handleClick(influencer)}
          >
            <img
              src={influencer.image}
              alt={influencer.name}
              className="influencer-image"
            />
            <p>{influencer.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Homepage;
