import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // For navigation back to homepage
import "./Profile.css";
import NewsSection from "./NewsSection";
import RecentContent from "./RecentContent";
import VoteSection from "./VoteSection";

function Profile({ influencerId }) {
  const [influencerData, setInfluencerData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const navigate = useNavigate(); // For navigation back to homepage

  // Fetch influencer data when component mounts or influencerId changes
  useEffect(() => {
    if (influencerId) {
      const fetchInfluencerData = async () => {
        try {
          setIsLoading(true);
          setError(null);

          // Replace this with your actual API endpoint
          const response = await fetch(
            `https://your-api.com/influencers/${influencerId}`
          );
          if (!response.ok) {
            throw new Error("Failed to fetch influencer data.");
          }

          const data = await response.json();
          setInfluencerData(data);
        } catch (err) {
          setError(err.message);
        } finally {
          setIsLoading(false);
        }
      };

      fetchInfluencerData();
    }
  }, [influencerId]);

  return (
    <div className="container">
      <header className="header">
        <h1>vibeCHECK</h1>
      </header>

      <div className="profile">
        {isLoading ? (
          <p>Loading influencer details...</p>
        ) : error ? (
          <p>Error: {error}</p>
        ) : influencerData ? (
          <>
            <img
              src={influencerData.image}
              alt={influencerData.name}
              className="influencer-image"
            />
            <h2>{influencerData.name}</h2>
            <div className="influencer-details">
              <p>ID: {influencerData.id}</p>
              <div className="vibe-score">
                Vibe Score: {influencerData.vibeScore}%
              </div>
            </div>
          </>
        ) : (
          <>
            <h2>No Influencer Selected</h2>
            <p>Please go back and select an influencer.</p>
          </>
        )}
      </div>

      {influencerData && (
        <>
          <div className="vibe-stats">
            <div>{influencerData.vibeScore}% Good Vibes</div>
            <div>{100 - influencerData.vibeScore}% Bad Vibes</div>
          </div>

          <VoteSection influencerId={influencerData.id} />
          <NewsSection influencerId={influencerData.id} />
          <RecentContent influencerId={influencerData.id} />
        </>
      )}

      <button
        className="return-home-button"
        onClick={() => navigate("/")}
        style={{ marginTop: "20px", padding: "10px", fontSize: "16px" }}
      >
        Return to Homepage
      </button>
    </div>
  );
}

export default Profile;
