import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import "./Profile.css";
import SearchBar from './SearchBar';
import NewsSection from './NewsSection';
import RecentComments from './RecentComments';

function Profile({ influencerId }) {
    const [influencerData, setInfluencerData] = useState(null);
    const [allInfluencers, setAllInfluencers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    // Fetch all influencers on component mount
    useEffect(() => {
        const fetchInfluencers = async () => {
            try {
                const response = await fetch('http://127.0.0.1:8000/Influencers');
                if (!response.ok) {
                    throw new Error('Failed to fetch influencers');
                }
                const data = await response.json();
                setAllInfluencers(data);
                setLoading(false);
            } catch (err) {
                console.error('Error fetching influencers:', err);
                setError('Failed to load influencers');
                setLoading(false);
            }
        };

        fetchInfluencers();
    }, []);

    // Update influencer data when ID changes
    useEffect(() => {
        if (influencerId && allInfluencers.length > 0) {
            const selectedInfluencer = allInfluencers.find(
                influencer => influencer.id === influencerId
            );
            setInfluencerData(selectedInfluencer || null);
        }
    }, [influencerId, allInfluencers]);

    // Handle search result
    const handleSearchResult = (selectedInfluencer) => {
        setInfluencerData(selectedInfluencer);
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div className="profile-container">

            {influencerData ? (
                <div className="profile-content">
                    <div className="profile-header">
                        <img 
                            src={influencerData.image_url} 
                            alt={influencerData.name} 
                            className="profile-image"
                        />
                        <div className="profile-info">
                            <h1>{influencerData.name}</h1>
                            <div className="vibe-score">
                                <h2>{influencerData.vibe_score}% Good Vibes</h2>
                            </div>
                        </div>
                    </div>

                    <div className="content-sections">
                        <NewsSection influencerId={influencerData.id} />
                        <RecentComments influencerId={influencerData.id} />
                    </div>
                </div>
            ) : (
                <div className="no-selection">
                    Please search for an influencer or select one to view their profile.
                </div>
            )}
        </div>
    );
}

export default Profile;
